CREATE TABLE IF NOT EXISTS latestTransaction(recipientName VARCHAR(1024) NOT NULL, recipientIban VARCHAR(1024) NOT NULL, recipientBic VARCHAR(1024) NOT NULL, senderName VARCHAR(1024) NOT NULL, senderIban VARCHAR(1024) NOT NULL, senderBic VARCHAR(1024) NOT NULL, valueInSmallestUnit BIGINT NOT NULL, currency VARCHAR(10) NOT NULL, bookingDate TIMESTAMP NOT NULL, intendedUse VARCHAR(1024) NOT NULL, PRIMARY KEY(recipientBic, recipientIban, bookingDate));
